import { useState } from 'react'
import './MyForm.css'

const MyForm = ({user}) => {
  // três gerenciamento de dados
  const [name, setName] = useState(user ? user.name : ''); 
  const [email, setEmail] = useState(user ? user.email : '');
  const [write, setWrite] = useState()
  const hundleName = (e) => { 
    setName(e.target.value); 
  }

  const bankData = []; 

  function hundleSubmit (e) {
    e.preventDefault(); 
    bankData.push(name, email, write);
    console.log(bankData); 

    // limpar form
    setName(''); 
    setEmail('');
    setWrite(''); 
  }

  return (
    <div>
        {/* criação de form */}
        <form onSubmit={hundleSubmit}>
          <div>
            <label htmlFor="name">Name: </label>
            <input 
            type="text"
            name='name'
            placeholder='Digite o seu nome...'
            onChange={hundleName}
            value={name}
            />
          </div>  
          {/* label envolvendo input */}
          <label>
            <span>Email:</span>
            <input type="email"
            name='email'
            placeholder='Digite o seu e-mail...' 
            onChange={(e) => setEmail(e.target.value)}
            value={email}
            />
          </label>  
          {/* Textarea in react */}
          <label>
            <textarea name="comments" placeholder='Digite um comentário...' cols="30" rows="10" onChange={(e) => setWrite(e.target.value)} value={write}></textarea>
          </label>

          {/* select input in react*/}

          <label>
            <span>Escolha uma linguagem...</span>
            <select name="languages">
                <option value="php"></option>
                <option value="js">JavaScript</option>
            </select>
          </label>  

          <input type="submit" value="Enviar" />
        </form>
    </div>
  )
}

export default MyForm
